/* My actual age */
function calcAge(dateString) {
  var birthday = +new Date(dateString);
  return ~~((Date.now() - birthday) / (31557600000));
}
var age = calcAge("Wed Aug 02 1995 09:00:00 GMT-0300 (BRT)");
document.getElementById("years-old").innerHTML = age.toString();

/*                       */
/* Mobile menu Show&Hide */
/*                       */

/* Get HTML elements */
var divCloseMenu = document.getElementById("divCloseMenu");
var itemCloseMenu = document.getElementById("itemCloseMenu");
var menu = document.getElementById("menu");
var menuIcon = document.getElementById("menuButton");

/* Get viewport props */
var windowWidth = window.innerWidth; //  Get viewport width

/* Set elements status variables n default values */
var menuState = false; // Is menu open?
var mobileMenu = false; // Is menu on mobile mode?


/* Checks for every window rezise */
window.onresize = function() { //  Updates viewport width on resize
  windowWidth = window.innerWidth;

  if (windowWidth <= 600) {
    mobileMenu = true;
  } else {
    mobileMenu = false;
  } // Sets variable to watch menu mode
  if (mobileMenu == false) {
    menu.style.transform = "translate(0)"; // Resets desktop menu translate on resize;
  } else {
    menu.style.transform = "translate(120%)";
    divCloseMenu.style.display = "none";
  } // Sets mobile menu to hide position on resize
};


/* Open menu func */
function openMenu() {
  var menuState = true;

  if (mobileMenu == true) {
    divCloseMenu.style.display = "block";
    setTimeout(function() {
      menu.style.transform = "translate(0)"; // Move menu to inside viewport
    }, 100);
  }
}

/* Open menu event listeners */
menuIcon.addEventListener("click", openMenu, false);


/* Close menu func */
function closeMenu() {
  var menuState = false;
  divCloseMenu.style.display = "none";

  if (mobileMenu == true) {
    setTimeout(function() {
      menu.style.transform = "translate(120%)"; // Move menu to outside viewport
    }, 100);
  }
}

/* Close menu event listeners */
divCloseMenu.addEventListener("click", closeMenu, false);
itemCloseMenu.addEventListener("click", closeMenu, false);
/* Portfolio Show&Hide */
var modal = document.getElementById("portfolioModal");
var menuAbout = document.getElementById("menuAbout");
var menuPortfolio = document.getElementById("menuPortfolio");
var divClosePortfolio = document.getElementById("divClosePortfolio");

var portfolioState = false;

function openPortfolio() {
  portfolioState = true;
  divClosePortfolio.style.display = "block";
  menuAbout.classList.remove("menu__list__item__link_active");
  menuPortfolio.classList.add("menu__list__item__link_active");
  modal.style.display = "grid";
  setTimeout(function() {
    modal.style.transform = "translate(0)";
  }, 100);
}

menuPortfolio.addEventListener("click", openPortfolio, false);
menuPortfolio.addEventListener("click", closeMenu, false);

function closePortfolio() {
  portfolioState = false;
  divClosePortfolio.style.display = "none";
  modal.style.transform = "translate(120%)";
  menuPortfolio.classList.remove("menu__list__item__link_active");
  menuAbout.classList.add("menu__list__item__link_active");
  setTimeout(function() {
    modal.style.display = "none";
  }, 600);
}

divClosePortfolio.addEventListener("click", closePortfolio, false);
menuAbout.addEventListener("click", closePortfolio, false);
menuAbout.addEventListener("click", closeMenu, false);